        return new Source("{% extends \"FOSUserBundle::layout.html.twig\" %}

{% block fos_user_content %}
<br>
<hr>
<span class=\"h5 card-title\">Definir Atributos Por Articulo Por Proceso</span>
<hr>
{% for message in app.flashes('sussecc') %}
\t<div class=\"alert alert-success\" role=\"alert\">
\t  {{ message }}
\t</div>
{% endfor %}
\t\t{{ form_start(form, {'attr': {'novalidate': 'novalidate'}}) }}
\t\t{{ form_widget(form) }}
\t\t{{ form_end(form) }}
<hr>
\t\t<table class=\"table table-striped table-bordered table-hover table-sm \">
\t\t\t<thead>
\t\t\t\t<tr>
\t\t\t\t\t<th>#</th>
\t\t\t\t\t<th>Proceso</th>
\t\t\t\t\t<th>Articulo</th>
\t\t\t\t\t<th>Atributo</th>

\t\t\t\t\t<th></th>
\t\t\t\t</tr>\t\t\t\t
\t\t\t</thead>
\t\t\t<tbody>
\t\t\t\t{% for l in lista %}
\t\t\t\t\t<tr>
\t\t\t\t\t\t<td>{{ loop.index }}</td>
\t\t\t\t\t\t<td>{{ l.proceso }}</td>
\t\t\t\t\t\t<td>{{ l.articulo }}</td>
\t\t\t\t\t\t<td>{{ l.atributosList }}</td>
\t\t\t\t\t\t
\t\t\t\t\t\t<td>
\t\t\t\t\t\t\t<a href=\"{{ path('bd_add_atributo_por_proceso_delete', { id : l.id })}}\" class=\"btn btn-danger btn-sm\">Eliminar</a>
\t\t\t\t\t\t</td>
\t\t\t\t\t</tr>
\t\t\t\t{% endfor %}
\t\t\t</tbody>
\t\t</table>
{% endblock fos_user_content %}

{% block javascripts %}

{% endblock %}
